mapper.update:
update comments set status = 1 where article_id in
#{id}
/>

mapper.select:
select * from comments

    
        and content like concat('%',#{keyword},'%') and username like concat('%',#{keyword},'%')
    
    
        and status = #{status}

        select * from comments
        
            
                and article_id = #{articleId}
            
            
                and status = #{status}
            
            
                and content = #{content}
            
             
                and username = #{username}
            
             
                and reply_id = #{replyId}
            

        
        order by create_time desc
